const ID_GAME="game-1",SUIT_ELEMENT="game-box__suit",ACTIVE_CLASS="game-box__suit--active",SUIT=["spades","clubs","diams","hearts"];function clickHandler(){$("#game-1 .game-box__suit").removeClass(ACTIVE_CLASS);const e=$(this);activateEl(e,getSuitClass(e)),"del"==$('[name="actions"]:checked').val()&&$("#game-1 ."+ACTIVE_CLASS).empty().removeClass(ACTIVE_CLASS)}function activateEl(e,t){for(const a of createData(e))t===a.class&&(a.element.addClass(ACTIVE_CLASS),a.active||activateEl(a.element,t))}function createData(e){const t=e.parent(),a=t.prev().find(`:eq(${e.index()})`),s=t.next().find(`:eq(${e.index()})`);return[e,e.prev(),e.next(),a,s].map(e=>({element:e,active:getStatusActive(e),class:getSuitClass(e)}))}function getClasses(e){return e.length?e.attr("class").split(/\s+/):[]}function getSuitClass(e){return getClasses(e).filter(e=>-1!==SUIT.indexOf(e)).join()}function getStatusActive(e){return-1!==getClasses(e).indexOf(ACTIVE_CLASS)}$("#game-1 .game-box__suit").on("click",clickHandler);