const SUIT_ELEMENT="game-box__suit",ACTIVE_CLASS="game-box__suit--active",SUIT=["spades","clubs","diams","hearts"];function clickHandler(){$(".game-box__suit").removeClass(ACTIVE_CLASS);const e=$(this);activateEl(e,getSuitClass(e)),"del"==$('[name="actions"]:checked').val()&&$("."+ACTIVE_CLASS).empty().removeClass(ACTIVE_CLASS)}function activateEl(e,t){for(const s of createData(e))t===s.class&&(s.element.addClass(ACTIVE_CLASS),s.active||activateEl(s.element,t))}function createData(e){const t=e.parent(),s=t.prev().find(`:eq(${e.index()})`),a=t.next().find(`:eq(${e.index()})`);return[e,e.prev(),e.next(),s,a].map(e=>({element:e,active:getStatusActive(e),class:getSuitClass(e)}))}function getClasses(e){return e.length?e.attr("class").split(/\s+/):[]}function getSuitClass(e){return getClasses(e).filter(e=>-1!==SUIT.indexOf(e)).join()}function getStatusActive(e){return-1!==getClasses(e).indexOf(ACTIVE_CLASS)}$(".game-box__suit").on("click",clickHandler);