const SUIT_ELEMENT="box__suit",ACTIVE_CLASS="box__suit--active",SUIT=["spades","clubs","diams","hearts"];function clickHandler(){$(".box__suit").removeClass(ACTIVE_CLASS);const t=$(this);activateEl(t,getSuitClass(t)),"del"==$('[name="actions"]:checked').val()&&$("."+ACTIVE_CLASS).empty().removeClass(ACTIVE_CLASS)}function activateEl(t,e){for(const s of createData(t))e===s.class&&(s.element.addClass(ACTIVE_CLASS),s.active||activateEl(s.element,e))}function createData(t){const e=t.parent(),s=e.prev().find(`:eq(${t.index()})`),a=e.next().find(`:eq(${t.index()})`);return[t,t.prev(),t.next(),s,a].map(t=>({element:t,active:getStatusActive(t),class:getSuitClass(t)}))}function getClasses(t){return t.length?t.attr("class").split(/\s+/):[]}function getSuitClass(t){return getClasses(t).filter(t=>-1!==SUIT.indexOf(t)).join()}function getStatusActive(t){return-1!==getClasses(t).indexOf(ACTIVE_CLASS)}$(".box__suit").on("click",clickHandler);